import{_ as i,b as t,o as a,Q as e}from"./chunks/framework.CUk_15SL.js";const o="/assets/image.7X40ygSb.png",l="/assets/image-2.CfGQmAZK.png",n="/assets/image-1.OVMsSuv5.png",d="/assets/image-3.D0rrWu5b.png",p="/assets/image-5.otDVcu-5.png",r="/assets/image-6.9sixjJF2.png",c="data:image/png;base64,",h="/assets/image-8.DacdGtoi.png",y=JSON.parse('{"title":"Window 重新打开已经关闭的应用通知","description":"Window 重新打开已经关闭的应用通知","frontmatter":{"id":24,"date":"2024/03/26 14:04","title":"Window 重新打开已经关闭的应用通知","tag":"Window使用","description":"Window 重新打开已经关闭的应用通知"},"headers":[],"relativePath":"Win11TurnOnNotificationsAgain.md","filePath":"Win11TurnOnNotificationsAgain.md"}'),k={name:"Win11TurnOnNotificationsAgain.md"};function g(A,s,m,u,b,E){return a(),t("div",null,s[0]||(s[0]=[e('<h1 id="window-重新打开已经关闭的应用通知" tabindex="-1">window 重新打开已经关闭的应用通知 <a class="header-anchor" href="#window-重新打开已经关闭的应用通知" aria-label="Permalink to &quot;window 重新打开已经关闭的应用通知&quot;">​</a></h1><h2 id="引言" tabindex="-1">引言 <a class="header-anchor" href="#引言" aria-label="Permalink to &quot;引言&quot;">​</a></h2><p>在使用个人开发软件 <a href="https://github.com/cosy247/DrinkWater" target="_blank" rel="noreferrer">DrinkWater</a> 时，不小心关闭了应用的通知。由于该软件没有被系统直接收录在通知管理中，我需要通过其他方式来重新开启通知。以下是我记录的重新打开已关闭应用通知的过程。</p><p><img src="'+o+'" alt="alt text"></p><h2 id="修改用户数据库数据" tabindex="-1">修改用户数据库数据 <a class="header-anchor" href="#修改用户数据库数据" aria-label="Permalink to &quot;修改用户数据库数据&quot;">​</a></h2><ol><li>文件路径：<code>C:\\Users\\用户名\\AppData\\Local\\Microsoft\\Windows\\Notifications\\wpndatabase.db</code></li></ol><p><img src="'+l+'" alt="alt text"></p><p>注意：C:\\Users\\用户名\\AppData 为隐藏文件夹，可以通过下图设置开启查看隐藏的文件和文件夹</p><p><img src="'+n+'" alt="alt text"></p><ol start="2"><li>由于 <code>.db</code> 文件是数据库文件，需要使用专门的软件来打开。这里使用 <a href="https://www.sqlitespy.com/download.html" target="_blank" rel="noreferrer">SQLiteSpy</a> ，也可以使用数据库管理工具。以下为 SQLiteSpy 打开上面的<code>wpndatabase.db</code>文件后的界面：</li></ol><p><img src="'+d+'" alt="alt text"></p><ol start="3"><li>点击左边的 <code>NotificationHandler</code> 后后边将出现表格，点击 <code>PrimaryId</code> 进行排序，找到对应软件的数据行并记录 <code>RecordId</code>，这里是 <code>282</code>。</li></ol><p><img src="'+p+'" alt="alt text"></p><ol start="4"><li>在上面空白区域写入</li></ol><div class="language-sql vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">UPDATE</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> HandlerSettings </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">SET</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> Value=</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">1</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> WHERE</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> HandlerId</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">282</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> AND</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> SettingKey</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;s:lock:toast&#39;</span></span></code></pre></div><p>需要将 <code>282</code> 替换为你需要修改软件的 <code>RecordId</code>。这段代码表示将左边 <code>HandlerSettings</code> 表中的 <code>282</code> 号软件的 <code>s:lock:toast</code> 对应值改为 <code>1</code>。<code>0</code> 表示关闭，<code>1</code> 表示开启。</p><p><img src="'+r+'" alt="alt text"></p><ol start="5"><li>选择 <code>Execute &gt; Execute SQL</code> 执行输入的代码端。可以执行以下代码查看是否更改成功</li></ol><div class="language-sql vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">SELECT</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> *</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> FROM</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> HandlerSettings </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">WHERE</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> HandlerId</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">282</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> AND</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> SettingKey</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;s:lock:toast&#39;</span></span></code></pre></div><ol start="6"><li><p>由于 win 版本不同，<code>SettingKey</code> 的名称可能不同。win11 为 <code>s:lock:toast</code>，win10 为 <code>s:toast</code>。在执行上面代码时替换即可，也可以将 <code>s:lock:toast</code> 和 <code>s:toast</code> 都执行一次。</p></li><li><p>关闭时出现下面弹窗询问<code>是否需要保存刚刚运行的代码</code>，这里选择 No 直接关闭即可。</p></li></ol><p><img src="'+c+'" alt="alt text"></p><h2 id="修改注册表的数据" tabindex="-1">修改注册表的数据 <a class="header-anchor" href="#修改注册表的数据" aria-label="Permalink to &quot;修改注册表的数据&quot;">​</a></h2><ol><li><p>使用组合键<code>window + r</code> 打开控制台并输入 <code>regedit</code> 打开<code>注册表编辑器</code>，或者直接搜索 <code>注册表编辑器</code>打开。</p></li><li><p>导航至以下路径：<code>HKEY_CURRENT_USER\\SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Notifications\\Settings</code>并点击左侧对应软件。</p></li></ol><p><img src="'+h+'" alt="alt text"></p><ol start="3"><li>将右方的<code>Enabled</code>行进行删除</li></ol><h2 id="测试" tabindex="-1">测试 <a class="header-anchor" href="#测试" aria-label="Permalink to &quot;测试&quot;">​</a></h2><p>重新打开你的应用并检查通知是否已重新开启。如果仍然没有通知，可以尝试重启电脑后再次检查。</p>',27)]))}const D=i(k,[["render",g]]);export{y as __pageData,D as default};
